					["custom"] = "aura_env.layout = \"flat\"\n-- Options are \"flat\" or \"box\" (box: 2x3, flat: one row)\naura_env.manual = false\n-- allows you to select positions manually\naura_env.specColours = true\n-- turns on spec specific colours\nlocal Blood = {\n    Foreground  = \"e43a3a\",\n    Recharge  = \"474747\",\n}\nlocal Frost = {\n    Foreground  = \"2c9bff\",\n    Recharge  = \"474747\",\n}\nlocal Unholy = {\n    Foreground  = \"3dff5a\",\n    Recharge  = \"474747\",\n}\naura_env.Spacing = 2.99\n-- How much space between shards \naura_env.shardX, aura_env.shardY = 0, 0\n-- X, Y of the text on the bar\naura_env.justifyH = \"CENTER\"\n-- Justify the text horizontally\n-- \"CENTER\", \"LEFT\" & \"RIGHT\"\naura_env.justifyV = \"MIDDLE\"\n-- Justify the text vertically\n-- \"MIDDLE\", \"TOP\" & \"BOTTOM\"\naura_env.position = \"CENTER\"\n-- the anchor point of the text\n-- \"TOP\", \"RIGHT\" \"BOTTOM\" & \"LEFT\": the center-points of the respective sides.\n-- \"TOPRIGHT\", \"TOPLEFT\", \"BOTTOMLEFT\" & \"BOTTOMRIGHT\": corners of the rectangle.\n-- \"CENTER\": the center point of the rectangle \naura_env.positions = {\n    [1] = {x = -120.66, t = 0 },\n    [2] = {x = -72.33, t = 0 },\n    [3] = {x = -24, t = 0 },\n    [4] = {x = 24, t = 0 },\n    [5] = {x = 72.33, t = 0 },\n    [6] = {x = 120.66, t = 0 },\n}\n--- Don't Touch ---\nfunction aura_env.AdjustToSpec(spec)\n    local function HexToRGBPerc(hex)\n        local string, tonumber = string, tonumber\n        local rhex, ghex, bhex = string.sub(hex, 1, 2), string.sub(hex, 3, 4), string.sub(hex, 5, 6)\n        \n        return {r = tonumber(rhex, 16)/255, g = tonumber(ghex, 16)/255, b = tonumber(bhex, 16)/255}\n    end\n    \n    local p = {\"Foreground\", \"Recharge\"}\n    spec = specialization\n    if  not aura_env.specColours then\n        \n        p.Foreground  = HexToRGBPerc(Frost.Foreground)\n        p.Recharge  = HexToRGBPerc(Frost.Recharge)\n    else\n        \n        if GetSpecialization() == 1 then\n            p.Foreground  = HexToRGBPerc(Blood.Foreground)\n            p.Recharge  = HexToRGBPerc(Blood.Recharge)\n        elseif GetSpecialization() == 2 then\n            p.Foreground  = HexToRGBPerc(Frost.Foreground)\n            p.Recharge  = HexToRGBPerc(Frost.Recharge)\n        elseif GetSpecialization() == 3 then\n            p.Foreground  = HexToRGBPerc(Unholy.Foreground)\n            p.Recharge  = HexToRGBPerc(Unholy.Recharge)\n            \n            \n        end\n        aura_env.Pallete = p\n    end\n    aura_env.Pallete = p\nend\naura_env.spec = select(2, GetSpecializationInfo(GetSpecialization()))\n\naura_env.AdjustToSpec(aura_env.spec)",
